mapper.select:

SELECT USERID, NAME, SSN, PASSWORD,
TEAMID, ROLL,GENDER, AGE, SUBJECT FROM MEMBER

	
		USERID LIKE #{userid}
	
	
		AND PASSWORD LIKE #{password}

mapper.update:
UPDATE MEMBER 
SET
PASSWORD = #{password}
,ROLL = #{roll}
,TEAMID = #{teamId}
WHERE USERID LIKE #{userid}

mapper.delete:
DELETE FROM MEMBER
WHERE USERID LIKE #{userid}
AND
PASSWORD LIKE #{password}

mapper.select:

SELECT COUNT(*) FROM MEMBER
WHERE USERID LIKE #{userid}
AND PASSWORD
LIKE #{password}
